/*FUNCTION**********************************************************************
*
* Function Name : init_coredebug_pins
* Description   : CoreDebug method sets registers according routing settings.
* Call this method code to route desired pins.
*END**************************************************************************/
void init_coredebug_pins(uint32_t instance)
{
  /* Affects PORTA_PCR0 register */
  PORT_HAL_SetMuxMode(PORTA,0UL,kPortMuxAlt7);
  /* Affects PORTA_PCR3 register */
  PORT_HAL_SetMuxMode(PORTA,3UL,kPortMuxAlt7);
}
/*FUNCTION**********************************************************************
*
* Function Name : deinit_coredebug_pins
* Description   : CoreDebug method sets registers according routing settings.
* Call this method code to disable routing of desired pins.
*END**************************************************************************/
void deinit_coredebug_pins(uint32_t instance)
{
  PORT_HAL_SetMuxMode(PORTA,0UL,kPortPinDisabled);
  PORT_HAL_SetMuxMode(PORTA,3UL,kPortPinDisabled);
}
/*FUNCTION**********************************************************************
*
* Function Name : init_gpio_pins
* Description   : GPIO method sets registers according routing settings.
* Call this method code to route desired pins.
*END**************************************************************************/
void init_gpio_pins(uint32_t instance)
{
  switch(instance) {    
    case GPIOA_IDX:                     /* GPIOA_IDX */
      /* Affects PORTA_PCR1 register */
      PORT_HAL_SetMuxMode(PORTA,1UL,kPortMuxAsGpio);
      PORT_HAL_SetPullMode(PORTA,1UL,kPortPullUp);
      PORT_HAL_SetPullCmd(PORTA,1UL,true);
      /* Affects PORTA_PCR2 register */
      PORT_HAL_SetMuxMode(PORTA,2UL,kPortMuxAsGpio);
      PORT_HAL_SetPullMode(PORTA,2UL,kPortPullUp);
      PORT_HAL_SetPullCmd(PORTA,2UL,true);
      break;
    case GPIOB_IDX:                     /* GPIOB_IDX */
      /* Affects PORTB_PCR0 register */
      PORT_HAL_SetDriveStrengthMode(PORTB,0UL,kPortLowDriveStrength);  
      PORT_HAL_SetMuxMode(PORTB,0UL,kPortMuxAsGpio);
      PORT_HAL_SetSlewRateMode(PORTB,0UL,kPortSlowSlewRate);
      /* Affects PORTB_PCR1 register */
      PORT_HAL_SetDriveStrengthMode(PORTB,1UL,kPortLowDriveStrength);  
      PORT_HAL_SetMuxMode(PORTB,1UL,kPortMuxAsGpio);
      PORT_HAL_SetSlewRateMode(PORTB,1UL,kPortSlowSlewRate);
      break;
    case GPIOC_IDX:                     /* GPIOC_IDX */
      /* Affects PORTC_PCR1 register */
      PORT_HAL_SetDriveStrengthMode(PORTC,1UL,kPortLowDriveStrength);  
      PORT_HAL_SetMuxMode(PORTC,1UL,kPortMuxAsGpio);
      PORT_HAL_SetSlewRateMode(PORTC,1UL,kPortSlowSlewRate);
      /* Affects PORTC_PCR2 register */
      PORT_HAL_SetDriveStrengthMode(PORTC,2UL,kPortLowDriveStrength);  
      PORT_HAL_SetMuxMode(PORTC,2UL,kPortMuxAsGpio);
      PORT_HAL_SetSlewRateMode(PORTC,2UL,kPortSlowSlewRate);
      /* Affects PORTC_PCR3 register */
      PORT_HAL_SetDriveStrengthMode(PORTC,3UL,kPortLowDriveStrength);  
      PORT_HAL_SetMuxMode(PORTC,3UL,kPortMuxAsGpio);
      PORT_HAL_SetSlewRateMode(PORTC,3UL,kPortSlowSlewRate);
      /* Affects PORTC_PCR4 register */
      PORT_HAL_SetDriveStrengthMode(PORTC,4UL,kPortLowDriveStrength);  
      PORT_HAL_SetMuxMode(PORTC,4UL,kPortMuxAsGpio);
      PORT_HAL_SetSlewRateMode(PORTC,4UL,kPortSlowSlewRate);
      break;
    case GPIOE_IDX:                     /* GPIOE_IDX */
      /* Affects PORTE_PCR30 register */
      PORT_HAL_SetDriveStrengthMode(PORTE,30UL,kPortLowDriveStrength);  
      PORT_HAL_SetMuxMode(PORTE,30UL,kPortMuxAsGpio);
      PORT_HAL_SetPullCmd(PORTE,30UL,true);
      PORT_HAL_SetSlewRateMode(PORTE,30UL,kPortSlowSlewRate);
      break;
    default:
      break;
  }
}
/*FUNCTION**********************************************************************
*
* Function Name : deinit_gpio_pins
* Description   : GPIO method sets registers according routing settings.
* Call this method code to disable routing of desired pins.
*END**************************************************************************/
void deinit_gpio_pins(uint32_t instance)
{
  switch(instance) {    
    case GPIOA_IDX:                     /* GPIOA_IDX */
      PORT_HAL_SetMuxMode(PORTA,1UL,kPortPinDisabled);
      PORT_HAL_SetMuxMode(PORTA,2UL,kPortPinDisabled);
      break;
    case GPIOB_IDX:                     /* GPIOB_IDX */
      PORT_HAL_SetMuxMode(PORTB,0UL,kPortPinDisabled);
      PORT_HAL_SetMuxMode(PORTB,1UL,kPortPinDisabled);
      break;
    case GPIOC_IDX:                     /* GPIOC_IDX */
      PORT_HAL_SetMuxMode(PORTC,1UL,kPortPinDisabled);
      PORT_HAL_SetMuxMode(PORTC,2UL,kPortPinDisabled);
      PORT_HAL_SetMuxMode(PORTC,3UL,kPortPinDisabled);
      PORT_HAL_SetMuxMode(PORTC,4UL,kPortPinDisabled);
      break;
    case GPIOE_IDX:                     /* GPIOE_IDX */
      PORT_HAL_SetMuxMode(PORTE,30UL,kPortPinDisabled);
      break;
    default:
      break;
  }
}

