  /* Affects PORTC_PCR1 register */
  PORT_HAL_SetDriveStrengthMode(PORTC,1UL,kPortLowDriveStrength);  
  PORT_HAL_SetMuxMode(PORTC,1UL,kPortMuxAsGpio);
  PORT_HAL_SetSlewRateMode(PORTC,1UL,kPortSlowSlewRate);
  /* Affects PORTC_PCR3 register */
  PORT_HAL_SetDriveStrengthMode(PORTC,3UL,kPortLowDriveStrength);  
  PORT_HAL_SetMuxMode(PORTC,3UL,kPortMuxAsGpio);
